mapper.update:
UPDATE EMP_BOARDS
   SET VIEW_CNT = #{viewCnt} + 1
 WHERE ID = #{id}

UPDATE EMP_BOARDS_STATISTICS
   SET LATEST_DATE1 = SYSDATE
 WHERE EMPID = #{empId}
   AND BID = #{bId}

UPDATE EMP_BOARDS_STATISTICS
   SET LATEST_DATE2 = SYSDATE


	     , ISLIKE = 'Y'


	   	 , ISLIKE = 'N'


 WHERE EMPID = #{empId}
   AND BID = #{bId}

UPDATE EMP_BOARDS
   SET TITLE = #{title}
     , CONTENT = #{content}
 WHERE ID = #{id}
